mapper.select:
SELECT tjt.id AS id, (SELECT tu.username FROM t_user tu WHERE tu.id = tjt.uid)AS username, (SELECT tu.real_name FROM t_user tu WHERE tu.id = tjt.uid)AS realName, (SELECT tr.name FROM t_role tr WHERE tr.id = (SELECT tu.rid FROM t_user tu WHERE tu.id = tjt.uid)) AS role, (SELECT td.name FROM t_department td WHERE td.id = (SELECT tu.rid FROM t_user tu WHERE tu.id = tjt.uid)) AS department, (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) AS year, (SELECT twp.queater_score FROM t_work_plan twp WHERE twp.year = (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) AND twp.queater = 1)AS month1, (SELECT twp.queater_score FROM t_work_plan twp WHERE twp.year = (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) AND twp.queater = 2)AS month2, (SELECT twp.queater_score FROM t_work_plan twp WHERE twp.year = (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) AND twp.queater = 3)AS month3, (SELECT twp.queater_score FROM t_work_plan twp WHERE twp.year = (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) AND twp.queater = 4)AS month4, (SELECT tp.summary_score FROM t_performance tp WHERE tp.year = (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id))AS summaryScore, (SELECT tar.total_score FROM t_ability_result tar WHERE tar.year =(SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id))AS abilityScore, (SELECT tp.year_score FROM t_performance tp WHERE tp.year = (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id))AS yearScore, (SELECT ttp.test_type_id FROM t_test_plan ttp WHERE ttp.id = tjt.test_id)AS testType FROM t_join_test tjt
AND (SELECT ttp.test_type_id FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) = 2
AND (SELECT tu.did FROM t_user tu WHERE tu.id = tjt.uid) = #{did}
AND (SELECT tu.rid FROM t_user tu WHERE tu.id = tjt.uid) = #{rid}
AND (SELECT ttp.year FROM t_test_plan ttp WHERE ttp.id = tjt.test_id) = #{year}
AND (SELECT tu.username FROM t_user tu WHERE tu.id = tjt.uid) = #{username}
